import React, { useState } from 'react';
import { useCRM } from '../context/CRMContext';
import { UserPlus, Mail, Shield, Upload } from 'lucide-react';

const CreateUser = () => {
  const { addUser } = useCRM();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'Engineer',
    avatarUrl: ''
  });
  const [previewUrl, setPreviewUrl] = useState('');
  const [errors, setErrors] = useState({});

  const roles = ['Super Admin', 'Admin', 'Coordinator', 'Engineer'];

  const validate = () => {
    const errs = {};
    if (!formData.name.trim()) errs.name = 'Name is required';
    if (!formData.email.trim()) errs.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) errs.email = 'Email is invalid';
    if (!formData.password) errs.password = 'Password is required';
    if (!formData.role) errs.role = 'Role is required';
    if (!formData.avatarUrl) errs.avatarUrl = 'Avatar is required';
    setErrors(errs);
    return Object.keys(errs).length === 0;
  };

  const handleFileChange = e => {
    const file = e.target.files[0];
    if (!file) return;
    // Convert to data URL for preview
    const reader = new FileReader();
    reader.onload = () => {
      setPreviewUrl(reader.result);
      setFormData({ ...formData, avatarUrl: reader.result });
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (!validate()) return;
    addUser({
      name: formData.name.trim(),
      email: formData.email.trim(),
      password: formData.password,
      role: formData.role,
      avatar: formData.avatarUrl
    });
    // Reset form
    setFormData({ name: '', email: '', password: '', role: 'Engineer', avatarUrl: '' });
    setPreviewUrl('');
    setErrors({});
  };

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow">
      <h1 className="text-2xl font-bold mb-4 flex items-center">
        <UserPlus className="w-6 h-6 mr-2" /> Create User
      </h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium">Full Name *</label>
          <input
            type="text"
            className={`input-field w-full ${errors.name ? 'border-red-500' : ''}`}
            value={formData.name}
            onChange={e => setFormData({ ...formData, name: e.target.value })}
          />
          {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
        </div>
        <div>
          <label className="block text-sm font-medium">Email *</label>
          <div className="flex items-center">
            <Mail className="w-5 h-5 mr-2 text-gray-400" />
            <input
              type="email"
              className={`input-field w-full ${errors.email ? 'border-red-500' : ''}`}
              value={formData.email}
              onChange={e => setFormData({ ...formData, email: e.target.value })}
            />
          </div>
          {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
        </div>
        <div>
          <label className="block text-sm font-medium">Password *</label>
          <input
            type="password"
            className={`input-field w-full ${errors.password ? 'border-red-500' : ''}`}
            value={formData.password}
            onChange={e => setFormData({ ...formData, password: e.target.value })}
          />
          {errors.password && <p className="text-red-500 text-xs mt-1">{errors.password}</p>}
        </div>
        <div>
          <label className="block text-sm font-medium">Role *</label>
          <div className="flex items-center space-x-2">
            <Shield className="w-5 h-5 text-gray-400" />
            <select
              className={`input-field w-full ${errors.role ? 'border-red-500' : ''}`}
              value={formData.role}
              onChange={e => setFormData({ ...formData, role: e.target.value })}
            >
              {roles.map(r => (
                <option key={r} value={r}>{r}</option>
              ))}
            </select>
          </div>
          {errors.role && <p className="text-red-500 text-xs mt-1">{errors.role}</p>}
        </div>
        <div>
          <label className="block text-sm font-medium">Avatar *</label>
          <div className="flex items-center space-x-2">
            <Upload className="w-5 h-5 text-gray-400" />
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className={`w-full ${errors.avatarUrl ? 'border-red-500' : ''}`}
            />
          </div>
          {errors.avatarUrl && <p className="text-red-500 text-xs mt-1">{errors.avatarUrl}</p>}
          {previewUrl && (
            <img
              src={previewUrl}
              alt="Avatar Preview"
              className="mt-3 w-24 h-24 rounded-full object-cover border"
            />
          )}
        </div>
        <div className="flex justify-end">
          <button type="submit" className="btn-primary">
            Create User
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateUser;
