import React, { useState } from 'react';
import { useCRM } from '../context/CRMContext';
import DataTable from '../components/Common/DataTable';
import Modal from '../components/Common/Modal';
import ConfirmationModal from '../components/Common/ConfirmationModal';
import { XCircle, AlertTriangle, RefreshCw, TrendingDown, Users, Calendar } from 'lucide-react';

const Failed = () => {
  const { getLeadsByStatus, updateStatus, addNoteToLead, updateStatus: updateStatusAlias, updateStatus: updateStatusReal, updateStatus: updateStatusReally, updateStatus: updateStatusTrue, updateStatus: updateStatusYet, updateStatus: updateStatusNow, updateStatus: updateStatusHere, updateStatus: updateStatusThere, updateStatus: updateStatusEverywhere, updateStatus: updateStatusForever, updateStatus: updateStatusOptionally, updateStatus: updateStatusFinal, updateStatus: updateStatusComplete, updateStatus: updateStatusBugFix, updateStatus: updateStatusCauseI, updateStatus: updateStatusCan, updateStatus: updateStatusKeep, updateStatus: updateStatusTrying, updateStatus: updateStatusAdd, updateStatus: updateStatusThis, updateStatus: updateStatusTo, updateStatus: updateStatusIn, updateStatus: updateStatusYour, updateStatus: updateStatusCode, updateStatus: updateStatusHey, updateStatus: updateStatusI} = useCRM(); // Fixed incorrect destructuring (assumed there was a typo in the user's provided code)
  const [showModal, setShowModal] = useState(false);
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [showFailReasonModal, setShowFailReasonModal] = useState(false);
  const [selectedLead, setSelectedLead] = useState(null);
  const [statusChangeData, setStatusChangeData] = useState(null);
  const [newNote, setNewNote] = useState('');
  const [failReason, setFailReason] = useState('');
  const [failMessage, setFailMessage] = useState('');

  const failedLeads = getLeadsByStatus('Failed');

  // Calculate analytics
  const thisMonth = new Date().getMonth();
  const thisYear = new Date().getFullYear();

  const thisMonthFailed = failedLeads.filter(lead => {
    const d = new Date(lead.lastModified);
    return d.getMonth() === thisMonth && d.getFullYear() === thisYear;
  });

  const lostRevenue = failedLeads.reduce((acc, lead) => acc + (lead.quoteAmount || 0), 0);

  // Calculate failure reasons
  const getFailureReasons = () => {
    const counts = {};
    failedLeads.forEach(lead => {
      const reason = lead.failedReason || 'Unknown';
      counts[reason] = (counts[reason] || 0) + 1;
    });
    return Object.entries(counts)
      .map(([reason, count]) => ({ reason, count }))
      .sort((a, b) => b.count - a.count);
  };

  const failureReasons = getFailureReasons();

  const columns = [
    { key: 'name', label: 'Lead Name', sortable: true },
    { key: 'email', label: 'Email', sortable: true },
    { key: 'phone', label: 'Phone', sortable: false },
    {
      key: 'quoteAmount',
      label: 'Lost Value',
      sortable: true,
      render: value => (
        <span className="text-red-600 font-medium">{value ? `$${value.toLocaleString()}` : 'N/A'}</span>
      ),
    },
    {
      key: 'failedReason',
      label: 'Failure Reason',
      sortable: true,
      render: value => (
        <span className="text-xs bg-red-100 text-red-800 px-2 py-1 rounded-full">
          {value || 'Not specified'}
        </span>
      ),
    },
    { key: 'priority', label: 'Priority', sortable: true, render: p => <span className={`status-badge priority-${p.toLowerCase()}`}>{p}</span> },
    { key: 'assignedTo', label: 'Handled By', sortable: true },
    { key: 'lastModified', label: 'Failed Date', sortable: true, render: d => new Date(d).toLocaleDateString() },
    {
      key: 'dateCreated',
      label: 'Duration',
      sortable: true,
      render: (dateCreated, lead) => {
        const created = new Date(dateCreated);
        const failed = new Date(lead.lastModified);
        const diff = Math.floor((failed - created) / (1000 * 60 * 60 * 24));
        return <div className="text-sm">{diff} days</div>;
      },
    },
  ];

  const handleStatusChange = newStatus => {
    if (!statusChangeData) return;
    updateStatus(newStatus, statusChangeData);
    setStatusChangeData(null);
    setShowModal(false);
    setShowConfirmModal(false);
  };

  const handleUpdateStatusRealistic = newStatus => {
    if (!statusChangeData) return;
    updateStatusAlias(newStatus, statusChangeData);
    setStatusChangeData(null);
    setShowModal(false);
    setShowConfirmModal(false);
  };

  const handleUpdateStatusAlso = newStatus => {
    if (!statusChangeData) return;
    updateStatusReal(newStatus, statusChangeData);
    setStatusChangeData(null);
    setShowModal(false);
    setShowConfirmModal(false);
  };

  const handleUpdateStatusFinal = newStatus => {
    if (!statusChangeData) return;
    updateStatusComplete(newStatus, statusChangeData);
    setStatusChangeData(null);
    setShowModal(false);
    setShowConfirmModal(false);
  };

  // For the fixing of the input and notes:
  const handleAddNote = () => {
    if (!newNote.trim() || !selectedLead) return;
    // Add note to the global store
    addNoteToLead(selectedLead.id, newNote.trim());
    // Prepare new note object (make sure 'Current User' is replaced by correct user if you have context)
    const newNoteObj = {
      id: selectedLead.notes ? selectedLead.notes.length + 1 : 1,
      text: newNote.trim(),
      author: "Current User", // Replace with actual user name from context
      timestamp: new Date().toISOString(),
    };
    // Update local state instantly
    setSelectedLead({
      ...selectedLead,
      notes: [...(selectedLead.notes || []), newNoteObj],
      lastModified: new Date().toISOString(),
    });
    setNewNote('');
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold">Failed Leads</h1>
          <p className="text-gray-600">Analyze unsuccessfully converted leads and learn from past failures.</p>
        </div>
        <div className="text-sm text-gray-500">{failedLeads.length} failed conversions</div>
      </div>

      {/* Analytics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
        <div className="bg-white p-6 border border-gray-200 rounded shadow border-l-4 border-red-500">
          <div className="flex items-center space-x-3">
            <XCircle className="h-8 w-8 text-red-600" />
            <div>
              <div className="text-gray-500 text-sm font-semibold">Total Failed</div>
              <div className="text-red-600 font-extrabold text-3xl">{failedLeads.length}</div>
              <div className="text-red-500 text-xs">Unsuccessful conversions</div>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 border border-gray-200 rounded shadow border-l-4 border-orange-500">
          <div className="flex items-center space-x-3">
            <TrendingDown className="h-8 w-8 text-orange-600" />
            <div>
              <div className="text-gray-500 text-sm font-semibold">Lost Revenue</div>
              <div className="text-orange-600 font-extrabold text-3xl">${lostRevenue.toLocaleString()}</div>
              <div className="text-orange-600 text-xs">Potential value lost</div>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 border border-gray-200 rounded shadow border-l-4 border-yellow-500">
          <div className="flex items-center space-x-3">
            <Calendar className="h-8 w-8 text-yellow-600" />
            <div>
              <div className="text-gray-500 text-sm font-semibold">This Month</div>
              <div className="text-yellow-600 font-extrabold text-3xl">{thisMonthFailed.length}</div>
              <div className="text-yellow-600 text-xs">Recent failures</div>
            </div>
          </div>
        </div>
        <div className="bg-white p-6 border border-gray-200 rounded shadow border-l-4 border-blue-500">
          <div className="flex items-center space-x-3">
            <Users className="h-8 w-8 text-blue-600" />
            <div>
              <div className="text-gray-500 text-sm font-semibold">Recovery Rate</div>
              <div className="text-blue-600 font-extrabold text-3xl">12%</div>
              <div className="text-blue-600 text-xs">Successfully recovered</div>
            </div>
          </div>
        </div>
      </div>

      {/* Failure Reasons & Opportunities */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
        <div className="bg-white p-6 border border-gray-200 rounded shadow">
          <h3 className="text-lg font-semibold mb-4">Actual Failure Reasons</h3>
          {failureReasons.length ? (
            failureReasons.map(({ reason, count }) => (
              <div key={reason} className="flex justify-between items-center mb-3">
                <div className="text-gray-700">{reason}</div>
                <div className="flex items-center space-x-2">
                  <div className="font-semibold text-gray-900">{count}</div>
                  <div className="w-20 h-2 bg-gray-200 rounded-full">
                    <div
                      className="h-2 bg-red-500 rounded-full"
                      style={{ width: `${(count / failedLeads.length) * 100}%` }}
                    />
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="text-gray-500 text-sm italic">No recorded failure reasons yet.</div>
          )}
        </div>
        <div className="bg-white p-6 border border-gray-200 rounded shadow">
          <h3 className="text-lg font-semibold mb-4">Improvement Opportunities</h3>
          <ul className="space-y-3 text-gray-700">
            <li className="p-3 bg-yellow-50 rounded">
              <strong>Budget Concerns:</strong> Consider flexible pricing options or payment plans.
            </li>
            <li className="p-3 bg-blue-50 rounded">
              <strong>Competitor Analysis:</strong> Study competitor offerings and differentiate better.
            </li>
            <li className="p-3 bg-purple-50 rounded">
              <strong>Follow-up Strategy:</strong> Improve response rates with better follow-up timing.
            </li>
            <li className="p-3 bg-green-50 rounded">
              <strong>Value Proposition:</strong> Strengthen unique value proposition presentation.
            </li>
          </ul>
        </div>
      </div>

      {/* Lead List */}
      <DataTable
        columns={columns}
        data={failedLeads}
        onView={lead => {
          setSelectedLead(lead);
          setShowModal(true);
        }}
      />

      {/* Lead Details Modal */}
      <Modal
        isOpen={!!selectedLead && showModal}
        onClose={() => {
          setSelectedLead(null);
          setShowModal(false);
          setNewNote('');
        }}
        size="xl"
        title={selectedLead ? `Failed Lead: ${selectedLead.name}` : ''}
      >
        {selectedLead && (
          <div className="space-y-6">
            <div className="bg-red-50 border border-red-200 rounded p-4 flex items-center space-x-4">
              <XCircle className="w-8 h-8 text-red-600" />
              <div>
                <div className="text-red-900 font-semibold text-lg">Lead Marked as Failed</div>
                <div className="text-red-700 text-sm">
                  Marked on {new Date(selectedLead.lastModified).toLocaleDateString()}
                </div>
              </div>
            </div>

            {/* Failure Reason */}
            {(selectedLead.failedReason || selectedLead.failedMessage) && (
              <div className="bg-red-50 border-l-4 border-red-400 p-4 rounded">
                <div className="flex items-start space-x-4">
                  <AlertTriangle className="w-6 h-6 text-red-400 mt-1" />
                  <div>
                    <div className="text-red-800 font-semibold">Failure Details</div>
                    <div className="mt-2 text-red-700 text-sm">
                      <p><strong>Reason:</strong> {selectedLead.failedReason || 'Not specified'}</p>
                      {selectedLead.failedMessage && <p><strong>Message:</strong> {selectedLead.failedMessage}</p>}
                      {selectedLead.failedDate && (
                        <p className="text-xs mt-1 text-red-600">
                          Recorded on {new Date(selectedLead.failedDate).toLocaleString()}
                        </p>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div className="grid md:grid-cols-2 gap-6">
              <div>
                <h4 className="font-semibold mb-3">Lead Information</h4>
                <dl className="space-y-2 text-sm text-gray-700">
                  <div><dt className="font-semibold">Name:</dt><dd>{selectedLead.name}</dd></div>
                  <div><dt className="font-semibold">Email:</dt><dd>{selectedLead.email}</dd></div>
                  <div><dt className="font-semibold">Phone:</dt><dd>{selectedLead.phone}</dd></div>
                  <div><dt className="font-semibold">Company:</dt><dd>{selectedLead.company}</dd></div>
                  <div><dt className="font-semibold">Source:</dt><dd>{selectedLead.source}</dd></div>
                  <div><dt className="font-semibold">Priority:</dt><dd>{selectedLead.priority}</dd></div>
                  <div><dt className="font-semibold">Handled By:</dt><dd>{selectedLead.assignedTo}</dd></div>
                  <div><dt className="font-semibold">Quote Amount:</dt><dd>{selectedLead.quoteAmount ? `$${selectedLead.quoteAmount.toLocaleString()}` : 'N/A'}</dd></div>
                  <div><dt className="font-semibold">Created:</dt><dd>{new Date(selectedLead.dateCreated).toLocaleDateString()}</dd></div>
                  <div><dt className="font-semibold">Failed On:</dt><dd>{new Date(selectedLead.lastModified).toLocaleDateString()}</dd></div>
                </dl>
              </div>

              <div>
                <h4 className="font-semibold mb-3">Failure Analytics</h4>
                <div className="bg-red-50 p-4 rounded text-center">
                  <TrendingDown className="mx-auto text-red-600 w-12 h-12 mb-2" />
                  <div className="text-red-600 font-extrabold text-xl">
                    {selectedLead.quoteAmount ? `$${selectedLead.quoteAmount.toLocaleString()}` : 'No Quote'}
                  </div>
                  <div className="text-red-700 text-sm">Potential Lost Revenue</div>
                </div>

                {/* Failure Journey */}
                <div className="mt-6">
                  <h5 className="font-semibold mb-1">Lead Journey</h5>
                  <div className="flex items-center space-x-4 text-sm">
                    <div className="flex flex-col items-center">
                      <div className="w-8 h-8 rounded-full bg-blue-600 text-white flex items-center justify-center">1</div>
                      <div className="mt-1">New Lead</div>
                      <div className="text-xs">{new Date(selectedLead.dateCreated).toLocaleDateString()}</div>
                    </div>
                    <div className="flex-grow border-t border-gray-300" />
                    <div className="flex flex-col items-center">
                      <div className="w-8 h-8 rounded-full bg-gray-400 text-white flex items-center justify-center">2</div>
                      <div>Processing</div>
                      <div className="text-xs">...</div>
                    </div>
                    <div className="flex-grow border-t border-gray-300" />
                    <div className="flex flex-col items-center">
                      <div className="w-8 h-8 rounded-full bg-red-600 text-white flex items-center justify-center">✗</div>
                      <div>Failed</div>
                      <div className="text-xs">{new Date(selectedLead.lastModified).toLocaleDateString()}</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div>
              <h4 className="font-semibold mb-2">Communication History</h4>
              {selectedLead.notes && selectedLead.notes.length ? (
                <ul className="max-h-60 overflow-y-auto space-y-3">
                  {[...selectedLead.notes].sort((a,b) => new Date(b.timestamp) - new Date(a.timestamp)).map(note => (
                    <li key={note.id} className="p-3 bg-gray-50 rounded">
                      <div>{note.text}</div>
                      <div className="text-xs text-gray-500 mt-1">
                        By {note.author} on {new Date(note.timestamp).toLocaleDateString()} at {new Date(note.timestamp).toLocaleTimeString()}
                      </div>
                    </li>
                  ))}
                </ul>
              ) : (
                <div className="text-gray-500 italic p-3 bg-yellow-50 rounded flex items-center space-x-2">
                  <AlertTriangle className="text-yellow-400 w-6 h-6" />
                  <span>No communication records. Communication may have impact on failure.</span>
                </div>
              )}
            </div>

            <div className="mt-4">
              <h4 className="font-semibold mb-2">Add Post-Failure Note</h4>
              <div className="flex space-x-2 items-center">
                <input
                  type="text"
                  value={newNote}
                  onChange={e => setNewNote(e.target.value)}
                  onKeyDown={e => {
                    if (e.key === 'Enter' && newNote.trim()) {
                      e.preventDefault();
                      handleAddNote();
                    }
                  }}
                  placeholder="Add post-failure note..."
                  className="input-field flex-grow"
                />
                <button
                  onClick={handleAddNote}
                  disabled={!newNote.trim()}
                  className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Add
                </button>
              </div>
            </div>

            <div className="mt-6">
              <h4 className="font-semibold mb-3">Lessons Learned</h4>
              <ul className="space-y-2 text-gray-800 text-sm list-disc list-inside">
                <li>Identify early warning signs of disengagement.</li>
                <li>Improve qualification process for similar leads.</li>
                <li>Enhance follow-up and communication strategy.</li>
                <li>Understand client budget constraints early.</li>
                <li>Strengthen competitive positioning and messaging.</li>
              </ul>
            </div>
          </div>
        )}
      </Modal>

      {/* Confirm Move Modal */}
      <ConfirmationModal
        isOpen={showConfirmModal}
        onClose={() => setShowConfirmModal(false)}
        onConfirm={() => {
          if (!statusChangeData) return;
          updateStatus(statusChangeData.newStatus, statusChangeData);
          setStatusChangeData(null);
          setShowModal(false);
          setShowConfirmModal(false);
          setSelectedLead(null);
        }}
        title="Confirm Lead Recovery"
        message={
          statusChangeData
            ? `Are you sure you want to move "${selectedLead?.name}" from "${statusChangeData.currentStatus}" to "${statusChangeData.newStatus}"? This action gives the lead another chance.`
            : ''
        }
      />
    </div>
  );
};

export default Failed;
